contract StakerERC20_NoOwner is ERC20, Ownable {

    bool public paused;
    address[] internal stakingAddresses;
    mapping(address => uint256) internal stakes;

    constructor(uint256 initialSupply) ERC20("StakerERC20_NoOwner", "SENO") {
        _mint(msg.sender, initialSupply);
        //if you wanted to fill the digits you can do NUMBER * 10**18
    }

    function setPaused(bool _paused) public { //onlyOwner
        paused = _paused;
    }

    //returns 0 if not a staker, index of the array if yes
    function isStakingAddress(address _address) 
    public view returns(bool, uint256)
    {
        require(paused == false, "Contract Paused");
        for (uint256 index = 0; index < stakingAddresses.length; index++)
        {
            if (_address == stakingAddresses[index]) return (true, index);
        }
        return (false, 0);
    }

    function addStakingAddress(address _stakerAddress) 
    private
    {
        require(paused == false, "Contract Paused");
        (bool _isStaking, ) =  isStakingAddress(_stakerAddress);
        if(!_isStaking) stakingAddresses.push(_stakerAddress);
    }

    function removeStakingAddress(address _stakeholder)
    private
    {
        require(paused == false, "Contract Paused");
        (bool _isStaking, uint256 index) = isStakingAddress(_stakeholder);
        if(_isStaking){
            stakingAddresses[index] = stakingAddresses[stakingAddresses.length - 1];
            stakingAddresses.pop();
        }
    }

    function stakeOf(address _stakingAddress)
    public view returns(uint256)
    {
        (bool _isStaking, ) =  isStakingAddress(_stakingAddress);
        if(!_isStaking) return 0;
        else            return stakes[_stakingAddress];
    }

    function totalStakes()
    external view returns(uint256)
    {
        uint256 _totalStakes = 0;
        for (uint256 index = 0; index < stakingAddresses.length; index += 1)
        {
            _totalStakes += stakes[stakingAddresses[index]];
        }
        return _totalStakes;
    }

    //increase stake if exists
    function createStake(uint256 _stake)
    external
    {
        require(paused == false, "Contract Paused");
        require(_stake > 0, "Stake is zero"); 
        _burn(msg.sender, _stake);
        if(stakes[msg.sender] == 0) addStakingAddress(msg.sender);
        stakes[msg.sender] += _stake;
    }

    function removeStake(uint256 _stake)
    external
    {
        require(paused == false, "Contract Paused");
        require(_stake > 0, "Stake is zero"); 
        require( _stake <= stakes[msg.sender], "Cannot remove more than is staked." );
        (bool _isStaking, ) = isStakingAddress(msg.sender);
        require(_isStaking, "Address is not staking");
        
        stakes[msg.sender] -= _stake;
        if(stakes[msg.sender] == 0) removeStakingAddress(msg.sender);
        _mint(msg.sender, _stake);
    }

    
    function mint(address account, uint256 amount) public { //onlyOwner
        require(paused == false, "Contract Paused");
        _mint(account, amount);
    }

    function burn(address account, uint256 amount) public { //onlyOwner
        require(paused == false, "Contract Paused");
        _burn(account, amount);
    }
    
